INSERT INTO `pages` VALUES (1,'home','pending','Index','index','<!-- wp:section -->\n<div class=\"wp-block-section md-section\"><div class=\"container\"><!-- wp:cgb/block-common-intro -->\n<div class=\"wp-block-cgb-block-common-intro row\"><style>\n						.title__title strong {\n							color: #dda307;\n						}\n					</style><div class=\"col-lg-9\"><div class=\"title title__title-page\"><h2 class=\"title__title\" style=\"color:#153976\">Cho dù bạn đang bắt đầu cuộc sống hay chuẩn bị tăng quỹ hưu trí, Bảo Việt có các lựa chọn bảo hiểm và đầu tư được thiết kế để giúp bạn đạt được mục tiêu của mình<strong> — cho ngày hôm nay và tương lai viên mãn.</strong></h2><p class=\"title__text\">Chúng tôi cam kết đồng hành cùng bạn qua từng giai đoạn như bắt đầu một gia đình, mua nhà, lập kế hoạch chuyến đi, lập kế hoạch cho chặng đường dài phía trước.</p></div></div></div>\n<!-- /wp:cgb/block-common-intro -->\n\n<!-- wp:cgb/block-common-shortcode {\"title\":\"SẢN PHẨM PHỔ BIẾN NHẤT\",\"shortcode\":\"[SPPB]\"} -->\n<div class=\"row\"><div class=\"col-lg-9 \"><div class=\"title title__title-page\"><h2>SẢN PHẨM PHỔ BIẾN NHẤT</h2></div></div></div>[SPPB]\n<!-- /wp:cgb/block-common-shortcode --></div></div>\n<!-- /wp:section -->\n\n<!-- wp:section {\"darkSkin\":true,\"backgroundType\":\"image\",\"solidColor\":\"#2a79c0\",\"gradient\":\"#0095DA,#005CAB\",\"mediaID\":21,\"mediaURL\":\"http://baovietnhantho.awe7.com/storage/8dd0c51c-f6ea-48e1-8fc7-ddbf3a60db85/bg-home.jpg\",\"overlay\":true,\"overlayType\":\"gradient\",\"opacity\":9,\"className\":\"pb-50\"} -->\n<div class=\"wp-block-section md-section md-skin-dark has-background pb-50\" style=\"background:linear-gradient(147.28deg, rgba(0, 149, 218, 0.9),rgba(0, 92, 171, 0.9)), url( http://baovietnhantho.awe7.com/storage/8dd0c51c-f6ea-48e1-8fc7-ddbf3a60db85/bg-home.jpg );background-size:cover\" id=\"lap-ke-hoach\"><div class=\"container\"><!-- wp:cgb/block-sanpham-lapkehoachtaichinh {\"title\":\"Mục tiêu của bạn\",\"contentItems\":[{\"url\":\"http://baovietnhantho.awe7.com/bao-ve-va-cham-soc-y-te\",\"iconId\":null,\"icon\":\"/img/svg/iconbox1-light.svg\",\"body\":\"Bảo vệ và chăm sóc y tế\"},{\"url\":\"http://baovietnhantho.awe7.com/tuong-lai-cho-con-tre\",\"iconId\":null,\"icon\":\"/img/svg/iconbox2-light.svg\",\"body\":\"Tương lai cho con trẻ\"},{\"url\":\"http://baovietnhantho.awe7.com/tiet-kiem-va-dau-tu\",\"iconId\":null,\"icon\":\"/img/svg/iconbox3-light.svg\",\"body\":\"Tiết kiệm và đầu tư\"},{\"url\":\"http://baovietnhantho.awe7.com/dam-bao-an-toan-tai-chinh\",\"iconId\":null,\"icon\":\"/img/svg/iconbox4-light.svg\",\"body\":\"Đảm bảo an toàn tài chính\"},{\"url\":\"http://baovietnhantho.awe7.com/nghi-huu-an-nhan\",\"iconId\":null,\"icon\":\"/img/svg/iconbox5-light.svg\",\"body\":\"Nghỉ hưu an nhàn\"}],\"className\":\"md-section md-skin-dark pb-50 section-giaiphap container\"} -->\n<div class=\"wp-block-cgb-block-sanpham-lapkehoachtaichinh display-flex-solution md-section md-skin-dark pb-50 section-giaiphap container\"><div class=\"solution-icon\"><h4 class=\"solution-icon__title\">Mục tiêu của bạn</h4><ul class=\"solution-icon__list\"><li class=\"solution-icon__icon\"><a href=\"http://baovietnhantho.awe7.com/bao-ve-va-cham-soc-y-te\"><img src=\"/img/svg/iconbox1-light.svg\" alt=\"\"/><p>Bảo vệ và chăm sóc y tế</p></a></li><li class=\"solution-icon__icon\"><a href=\"http://baovietnhantho.awe7.com/tuong-lai-cho-con-tre\"><img src=\"/img/svg/iconbox2-light.svg\" alt=\"\"/><p>Tương lai cho con trẻ</p></a></li><li class=\"solution-icon__icon\"><a href=\"http://baovietnhantho.awe7.com/tiet-kiem-va-dau-tu\"><img src=\"/img/svg/iconbox3-light.svg\" alt=\"\"/><p>Tiết kiệm và đầu tư</p></a></li><li class=\"solution-icon__icon\"><a href=\"http://baovietnhantho.awe7.com/dam-bao-an-toan-tai-chinh\"><img src=\"/img/svg/iconbox4-light.svg\" alt=\"\"/><p>Đảm bảo an toàn tài chính</p></a></li><li class=\"solution-icon__icon\"><a href=\"http://baovietnhantho.awe7.com/nghi-huu-an-nhan\"><img src=\"/img/svg/iconbox5-light.svg\" alt=\"\"/><p>Nghỉ hưu an nhàn</p></a></li></ul></div><div class=\"row\"><div class=\"col-lg-6 col-xs-offset-0 col-sm-offset-0 col-md-offset-0 col-lg-offset-3 \">[LKHTC]</div></div></div>\n<!-- /wp:cgb/block-sanpham-lapkehoachtaichinh --></div></div>\n<!-- /wp:section -->\n\n<!-- wp:section {\"customBackgroundColor\":\"#eff3f3\"} -->\n<div class=\"wp-block-section md-section has-background\" style=\"background-color:#eff3f3\"><div class=\"container\"><!-- wp:cgb/block-home-about {\"contentItems\":[{\"fileURL\":\"/bao-viet-nhan-tho\",\"fileText\":\"Lịch sử phát triển\"},{\"fileURL\":\"/tam-nhin-va-su-menh\",\"fileText\":\"Tầm nhìn và sứ mệnh\"},{\"fileURL\":\"/ban-lanh-dao\",\"fileText\":\"Ban lãnh đạo\"},{\"fileURL\":\"/bao-cao-tai-chinh\",\"fileText\":\"Báo cáo tài chính\"},{\"fileURL\":\"/tin-tuc/hoat-dong-cong-dong\",\"fileText\":\"Hoạt động cộng đồng\"}],\"contentItems2\":[{\"number\":\"23 năm\",\"text\":\"Là đơn vị tiên phong trong lĩnh vực bảo hiểm nhân thọ tại Việt Nam\"},{\"number\":\"4.150 tỷ\",\"text\":\"Sự vững mạnh về nền tảng tài chính với số tổng vốn điều lệ\"},{\"number\":\"300+\",\"text\":\"Điểm phục vụ là hệ thống mạng lưới phủ khắp 64 tỉnh thành\"}],\"tcImage\":\"http://baovietnhantho.awe7.com/storage/8de8c9d0-f1bf-4c69-86cb-7a8ea19d9ee4/bao-cao-tai-chinh-2018-1.png\",\"tcUrl\":\"/storage/8de8c865-81fa-4b3a-ace2-3a9527e230a0/BVNT-Bao-cao-Tai-chinh-2018.pdf\",\"gtUrl1\":\"/tin-tuc/tin-tuc-tong-hop/bao-viet-nhan-tho-nhan-giai-thuong-do-tap-chi-tai-chinh-va-ngan-hang-toan-cau-cua-anh-quoc-binh-chon\",\"gtUrl2\":\"/Thong-tin-doanh-nghiep/TONG-CONG-TY-BAO-VIET-NHAN-THO-Chart\\u002d\\u002d900-2016.html\"} -->\n<div class=\"wp-block-cgb-block-home-about about-text\"><div class=\"row row-eq-height\"><div class=\"col-sm-7 col-md-8 col-lg-8 \"><div class=\"about-text__content\"><div class=\"title title__style-02\"><h2 class=\"title__title\">Là doanh nghiệp phát hành hợp đồng bảo hiểm nhân thọ đầu tiên trên thị trường Việt Nam</h2><p class=\"title__text\">Năm 1996, Bảo Việt Nhân thọ hiện giờ là một trong số ít các doanh nghiệp bảo hiểm nhân thọ Việt Nam có lịch sự hoạt động lâu năm và ngày càng khẳng định vị thế dẫn đầu thị trường.</p></div><div class=\"row\"><div class=\"col-sm-6 col-md-4 col-lg-4 \"><div class=\"about-text__item\"><h3 class=\"about-text__titlemin\">Giới thiệu về bảo việt</h3><ul class=\"about-text__list\"><li><a href=\"/bao-viet-nhan-tho\">Lịch sử phát triển</a></li><li><a href=\"/tam-nhin-va-su-menh\">Tầm nhìn và sứ mệnh</a></li><li><a href=\"/ban-lanh-dao\">Ban lãnh đạo</a></li><li><a href=\"/bao-cao-tai-chinh\">Báo cáo tài chính</a></li><li><a href=\"/tin-tuc/hoat-dong-cong-dong\">Hoạt động cộng đồng</a></li></ul></div></div><div class=\"col-sm-6 col-md-8 col-lg-7 \"><div class=\"row\"><div class=\"col-xs-6 col-sm-12 col-md-6 col-lg-6  col-xsx-12\"><div class=\"about-text__item\"><h3 class=\"about-text__titlemin\">Báo cáo tài chính</h3><div class=\"row\"><div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6 \"><div class=\"report report__style-02\"><div class=\"report__media\"><a href=\"/storage/8de8c865-81fa-4b3a-ace2-3a9527e230a0/BVNT-Bao-cao-Tai-chinh-2018.pdf\"><img src=\"http://baovietnhantho.awe7.com/storage/8de8c9d0-f1bf-4c69-86cb-7a8ea19d9ee4/bao-cao-tai-chinh-2018-1.png\" alt=\"\"/></a></div><h4 class=\"report__title\"><a href=\"/storage/8de8c865-81fa-4b3a-ace2-3a9527e230a0/BVNT-Bao-cao-Tai-chinh-2018.pdf\">Báo cáo thường niên 2018</a></h4></div></div></div></div></div><div class=\"col-xs-6 col-sm-12 col-md-6 col-lg-6  col-xsx-12\"><div class=\"about-text__item\"><h3 class=\"about-text__titlemin\">Giải thưởng</h3><div class=\"row\"><div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6 \"><div class=\"report report__style-02\"><div class=\"report__media\"><a href=\"/tin-tuc/tin-tuc-tong-hop/bao-viet-nhan-tho-nhan-giai-thuong-do-tap-chi-tai-chinh-va-ngan-hang-toan-cau-cua-anh-quoc-binh-chon\"><img src=\"/img/image/giaithuong.jpg\" alt=\"\"/></a></div><h4 class=\"report__title\"><a href=\"/tin-tuc/tin-tuc-tong-hop/bao-viet-nhan-tho-nhan-giai-thuong-do-tap-chi-tai-chinh-va-ngan-hang-toan-cau-cua-anh-quoc-binh-chon\">Công ty Bảo hiểm Nhân thọ Tốt nhất Việt Nam 2017</a></h4></div></div><div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6 \"><div class=\"report report__style-02\"><div class=\"report__media\"><a href=\"/Thong-tin-doanh-nghiep/TONG-CONG-TY-BAO-VIET-NHAN-THO-Chart--900-2016.html\"><img src=\"/img/image/giaithuong2.jpg\" alt=\"\"/></a></div><h4 class=\"report__title\"><a href=\"/Thong-tin-doanh-nghiep/TONG-CONG-TY-BAO-VIET-NHAN-THO-Chart--900-2016.html\">Top 500 doanh nghiệp lớn nhất Việt Nam</a></h4></div></div></div></div></div></div></div></div></div></div><div class=\"col-sm-5 col-md-4 col-lg-4 \"><div class=\"about-text__sidebar\"><div class=\"featured-number featured-number__style-02\"><span class=\"featured-number__number\">23 năm</span><p class=\"featured-number__text\">Là đơn vị tiên phong trong lĩnh vực bảo hiểm nhân thọ tại Việt Nam</p></div><div class=\"featured-number featured-number__style-02\"><span class=\"featured-number__number\">4.150 tỷ</span><p class=\"featured-number__text\">Sự vững mạnh về nền tảng tài chính với số tổng vốn điều lệ</p></div><div class=\"featured-number featured-number__style-02\"><span class=\"featured-number__number\">300+</span><p class=\"featured-number__text\">Điểm phục vụ là hệ thống mạng lưới phủ khắp 64 tỉnh thành</p></div></div></div></div></div>\n<!-- /wp:cgb/block-home-about --></div></div>\n<!-- /wp:section -->\n\n<!-- wp:group {\"className\":\"md-section md-text-center\"} -->\n<div class=\"wp-block-group md-section md-text-center\"><div class=\"wp-block-group__inner-container\"><!-- wp:cgb/block-home-baovegdv {\"tabItems\":[{\"iconId\":null,\"icon\":\"/img/image/infonew.jpg\",\"title\":\"Tập luyện\",\"url\":\"https://baovegiadinhviet.com/tap-luyen\"},{\"iconId\":null,\"icon\":\"/img/image/infonew2.jpg\",\"title\":\"Cẩm nang sống khỏe\",\"url\":\"https://baovegiadinhviet.com/cam-nang-song-khoe\"},{\"iconId\":null,\"icon\":\"/img/image/infonew3.jpg\",\"title\":\"Bảo vệ cuộc sống\",\"url\":\"https://baovegiadinhviet.com/bao-ve-cuoc-song\"},{\"iconId\":null,\"icon\":\"/img/image/infonew4.jpg\",\"title\":\"Phụ nữ 24h\",\"url\":\"https://baovegiadinhviet.com/phu-nu-24h\"}],\"btnlink\":\"https://baovegiadinhviet.com/\"} -->\n<div class=\"wp-block-cgb-block-home-baovegdv container\"><div class=\"row\"><div class=\"col-xs-6 col-lg-2 col-xs-offset-3 col-sm-offset-3 col-md-offset-3 col-lg-offset-5 \"><img src=\"/img/image/logobaovegiadinh.png\" alt=\"\"/></div></div><div class=\"row\"><div class=\"col-lg-8 col-xs-offset-0 col-sm-offset-0 col-md-offset-0 col-lg-offset-2 \"><div class=\"title title__title-color md-text-center\"><h2 class=\"title__title\">Trang cung cấp thông tin bảo hiểm,<br>sức khoẻ cho người Việt</h2></div></div></div><div class=\"image-link-web\"><div class=\"row\"><div class=\"col-xs-6 col-lg-3\"><div class=\"image image__background\"><a class=\"image__image\" href=\"https://baovegiadinhviet.com/tap-luyen\" style=\"background-image:url(/img/image/infonew.jpg)\"><img src=\"/img/image/infonew.jpg\" alt=\"\"/><span class=\"image__caption\">Tập luyện</span></a></div></div><div class=\"col-xs-6 col-lg-3\"><div class=\"image image__background\"><a class=\"image__image\" href=\"https://baovegiadinhviet.com/cam-nang-song-khoe\" style=\"background-image:url(/img/image/infonew2.jpg)\"><img src=\"/img/image/infonew2.jpg\" alt=\"\"/><span class=\"image__caption\">Cẩm nang sống khỏe</span></a></div></div><div class=\"col-xs-6 col-lg-3\"><div class=\"image image__background\"><a class=\"image__image\" href=\"https://baovegiadinhviet.com/bao-ve-cuoc-song\" style=\"background-image:url(/img/image/infonew3.jpg)\"><img src=\"/img/image/infonew3.jpg\" alt=\"\"/><span class=\"image__caption\">Bảo vệ cuộc sống</span></a></div></div><div class=\"col-xs-6 col-lg-3\"><div class=\"image image__background\"><a class=\"image__image\" href=\"https://baovegiadinhviet.com/phu-nu-24h\" style=\"background-image:url(/img/image/infonew4.jpg)\"><img src=\"/img/image/infonew4.jpg\" alt=\"\"/><span class=\"image__caption\">Phụ nữ 24h</span></a></div></div></div></div><a class=\"btn btn-secondary\" href=\"https://baovegiadinhviet.com/\" target=\"_blank\">Xem Trang Web</a></div>\n<!-- /wp:cgb/block-home-baovegdv --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:section {\"darkSkin\":true,\"backgroundType\":\"gradient\",\"gradient\":\"#0095DA,#005CAB\"} -->\n<div class=\"wp-block-section md-section md-skin-dark has-background\" style=\"background:linear-gradient(147.28deg, #0095DA,#005CAB)\"><div class=\"container\"><!-- wp:cgb/block-home-chonchuyenvien {\"phone\":\"1900 55 88 99 ext 4\",\"phone2\":\"1800 6966\"} -->\n<div class=\"wp-block-cgb-block-home-chonchuyenvien consultants-section\"><div class=\"consultants-section__item\"><div class=\"consultants-section__info\"><div class=\"consultants-section__avatar\"><img src=\"/img/image/avatarchuyenvientuvan.png\" alt=\"\"/></div><h3 class=\"consultants-section__title\">Chọn chuyên viên tư vấn cho riêng bạn</h3><p class=\"consultants-section__text\">Chủ động đặt hẹn với chuyên viên tài chính Bảo Việt để được lắng nghe và thấu hiểu</p><a href=\"/chuyen-vien\" class=\"btn btn-secondary\">Bắt đầu ngay</a></div></div><div class=\"consultants-section__item\"><ul class=\"consultants-section__contact\"><li class=\"first\"><h4>1900 55 88 99 ext 4</h4><p class=\"text1\">Tổng đài chăm sóc khách hàng</p><p class=\"text2\">Mọi thắc mắc của bạn được chuyên gia giải đáp một cách thấu đáo nhất.Hãy liên lạc cho chúng tôi</p></li><li class=\"last\"><h4>1800 6966</h4><p class=\"text2\">* Miễn phí với khách hàng đã có hợp đồng với Bảo Việt nhân thọ</p></li></ul></div></div>\n<!-- /wp:cgb/block-home-chonchuyenvien --></div></div>\n<!-- /wp:section -->\n\n<!-- wp:group {\"className\":\"md-section bg-gray\"} -->\n<div class=\"wp-block-group md-section bg-gray\"><div class=\"wp-block-group__inner-container\"><!-- wp:cgb/block-home-dichvukhachhang {\"media1ID\":15,\"media2ID\":18,\"media3ID\":19} -->\n<div class=\"wp-block-cgb-block-home-dichvukhachhang undefined container\"><div class=\"row\"><div class=\"col-lg-6 col-xs-offset-0 col-sm-offset-0 col-md-offset-0 col-lg-offset-3 \"><div class=\"title title__title-page md-text-center\"><h2 class=\"title__title\">Dịch vụ khách hàng</h2></div></div></div><div class=\"row row-eq-height\"><div class=\"col-sm-6 col-md-4 col-lg-4 \"><div class=\"service service1\"><div class=\"service__inner\"><div class=\"service__icon\"><img src=\"http://baovietnhantho.awe7.com/storage/8dd092f0-0e63-4f97-b8a4-25ee029e1f21/icon-service.png\"/></div><h3 class=\"service__title\"><a href=\"/dich-vu-khach-hang/huong-dan-giai-quyet-quyen-loi-bao-hiem\">Giải quyết quyền lợi bảo hiểm</a></h3><p class=\"service__text\">Bảo Việt Nhân thọ đã áp dụng Quy trình Giải quyết quyền lợi trong 15 phút để phục vụ Quý khách.</p></div></div></div><div class=\"col-sm-6 col-md-4 col-lg-4 \"><div class=\"service service2\"><div class=\"service__inner\"><div class=\"service__icon\"><img src=\"http://baovietnhantho.awe7.com/storage/8dd093d6-46f1-47a1-944a-32b1b845b98c/iconbox8.svg\"/></div><h3 class=\"service__title\"><a href=\"/dich-vu-khach-hang#thay-doi-thong-tin-hop-dong-bao-hiem\">Thay đổi thông tin bảo hiểm</a></h3><p class=\"service__text\"> Để thay đổi điều kiện hợp đồng trong hợp đồng bảo hiểm, Quý khách vui lòng thực hiện theo hướng dẫn.</p></div></div></div><div class=\"col-sm-6 col-md-4 col-lg-4 \"><div class=\"service service3\"><div class=\"service__inner\"><div class=\"service__icon\"><img src=\"http://baovietnhantho.awe7.com/storage/8dd09421-1f81-4ac5-b21d-540f4ff8bef2/iconbox9.svg\"/></div><h3 class=\"service__title\"><a href=\"/dich-vu-khach-hang#kenh-thanh-toan\">Hướng dẫn đóng phí bảo hiểm</a></h3><p class=\"service__text\"> Quý khách có thể lựa chọn một trong những hình thức thanh toán phí bảo hiểm phù hợp theo hướng dẫn.  </p></div></div></div></div></div>\n<!-- /wp:cgb/block-home-dichvukhachhang --></div></div>\n<!-- /wp:group -->','<!-- wp:section -->\n<div class=\"wp-block-section md-section\"><div class=\"container\"><!-- wp:cgb/block-common-intro -->\n<div class=\"wp-block-cgb-block-common-intro row\"><style>\n						.title__title strong {\n							color: #dda307;\n						}\n					</style><div class=\"col-lg-9\"><div class=\"title title__title-page\"><h2 class=\"title__title\" style=\"color:#153976\">Cho dù bạn đang bắt đầu cuộc sống hay chuẩn bị tăng quỹ hưu trí, Bảo Việt có các lựa chọn bảo hiểm và đầu tư được thiết kế để giúp bạn đạt được mục tiêu của mình<strong> — cho ngày hôm nay và tương lai viên mãn.</strong></h2><p class=\"title__text\">Chúng tôi cam kết đồng hành cùng bạn qua từng giai đoạn như bắt đầu một gia đình, mua nhà, lập kế hoạch chuyến đi, lập kế hoạch cho chặng đường dài phía trước.</p></div></div></div>\n<!-- /wp:cgb/block-common-intro -->\n\n<!-- wp:cgb/block-common-shortcode {\"title\":\"SẢN PHẨM PHỔ BIẾN NHẤT\",\"shortcode\":\"[SPPB]\"} -->\n<div class=\"row\"><div class=\"col-lg-9 \"><div class=\"title title__title-page\"><h2>SẢN PHẨM PHỔ BIẾN NHẤT</h2></div></div></div>[SPPB]\n<!-- /wp:cgb/block-common-shortcode --></div></div>\n<!-- /wp:section -->\n\n<!-- wp:section {\"darkSkin\":true,\"backgroundType\":\"image\",\"solidColor\":\"#2a79c0\",\"gradient\":\"#0095DA,#005CAB\",\"mediaID\":21,\"mediaURL\":\"http://baovietnhantho.awe7.com/storage/8dd0c51c-f6ea-48e1-8fc7-ddbf3a60db85/bg-home.jpg\",\"overlay\":true,\"overlayType\":\"gradient\",\"opacity\":9,\"className\":\"pb-50\"} -->\n<div class=\"wp-block-section md-section md-skin-dark has-background pb-50\" style=\"background:linear-gradient(147.28deg, rgba(0, 149, 218, 0.9),rgba(0, 92, 171, 0.9)), url( http://baovietnhantho.awe7.com/storage/8dd0c51c-f6ea-48e1-8fc7-ddbf3a60db85/bg-home.jpg );background-size:cover\" id=\"lap-ke-hoach\"><div class=\"container\"><!-- wp:cgb/block-sanpham-lapkehoachtaichinh {\"title\":\"Mục tiêu của bạn\",\"content