INSERT INTO `services` VALUES (6,'pending','Hướng dẫn tra cứu thông tin hợp đồng','huong-dan-tra-cuu-thong-tin-hop-dong','<!-- wp:section {\"className\":\"pt-0\"} -->\n<div class=\"wp-block-section md-section pt-0\"><div class=\"container\"><!-- wp:cgb/block-dichvu-intro {\"contentItems\":[{\"fileURL\":\"https://epos.baovietnhantho.com.vn/#/pages/open-help-center\",\"fileText\":\"Hướng dẫn My BVLIFE\"}],\"title\":\"Hướng dẫn tra cứu thông tin hợp đồng\",\"text\":\"- Đăng nhập My BVLife trên máy tính hoặc qua ứng dụng trên Apple Store trên iPhone hoặc CH Play trên máy điện thoại Android.\\u003cbr\\u003e- Chọn vào \\u003cstrong\\u003eTruy vấn Hợp đồng\\u003c/strong\\u003e/ tài khoản cá nhân \\u003cbr\\u003e- Màn hình hiển thị Danh sách các Hợp đồng bảo hiểm mà Khách hàng tham gia với Bảo Việt Nhân thọ \\u003cbr\\u003e- Chọn thông tin tra cứu tại Menu:\\u003cbr\\u003e [] Thông tin chung: Thông tin chung về Hợp đồng, về Quyền lợi bảo hiểm, về Phí bảo hiểm của Hợp đồng.\\u003cbr\\u003e [] Thông tin khách hàng: Thông tin Người được bảo hiểm, Bên mua bảo hiểm, Người thụ hưởng.\\u003cbr\\u003e [] Giá trị tài khoản.\\u003cbr\\u003e [] Quá trình đóng phí.\\u003cbr\\u003e [] Phí và Chi phí.\\u003cbr\\u003e [] Giải quyết quyền lợi bảo hiểm.\\u003cbr\\u003e [] Hoá đơn điện tử: Hoá đơn của các kỳ phí đã thanh toán. \\u003cbr\\u003e [] Thông báo thường niên. \\u003cbr\\u003e\\u003cbr\\u003e\",\"button\":\"Đăng nhập My BVLIFE\",\"buttonURL\":\"https://epos.baovietnhantho.com.vn/#/pages/login\"} -->\n<div class=\"row\"><div class=\"col-sm-8 col-md-8 col-lg-8 \"><h3 class=\"title-detail-min\">Hướng dẫn tra cứu thông tin hợp đồng</h3><p>- Đăng nhập My BVLife trên máy tính hoặc qua ứng dụng trên Apple Store trên iPhone hoặc CH Play trên máy điện thoại Android.<br>- Chọn vào <strong>Truy vấn Hợp đồng</strong>/ tài khoản cá nhân <br>- Màn hình hiển thị Danh sách các Hợp đồng bảo hiểm mà Khách hàng tham gia với Bảo Việt Nhân thọ <br>- Chọn thông tin tra cứu tại Menu:<br> [] Thông tin chung: Thông tin chung về Hợp đồng, về Quyền lợi bảo hiểm, về Phí bảo hiểm của Hợp đồng.<br> [] Thông tin khách hàng: Thông tin Người được bảo hiểm, Bên mua bảo hiểm, Người thụ hưởng.<br> [] Giá trị tài khoản.<br> [] Quá trình đóng phí.<br> [] Phí và Chi phí.<br> [] Giải quyết quyền lợi bảo hiểm.<br> [] Hoá đơn điện tử: Hoá đơn của các kỳ phí đã thanh toán. <br> [] Thông báo thường niên. <br><br></p><br/><a href=\"https://epos.baovietnhantho.com.vn/#/pages/login\" class=\"btn btn-secondary\">Đăng nhập My BVLIFE</a><br/><br/></div><div class=\"col-sm-4 col-md-4 col-lg-3 col-xs-offset-0 col-sm-offset-0 col-md-offset-0 col-lg-offset-1\"><h3 class=\"title-detail-min\">Tài liệu và biểu mẫu</h3><div class=\"widget\"><ul class=\"widget-file\"><li><a href=\"https://epos.baovietnhantho.com.vn/#/pages/open-help-center\"><i class=\"fa fa-file\"></i>Hướng dẫn My BVLIFE</a></li></ul></div></div></div><br/><br/><br/>\n<!-- /wp:cgb/block-dichvu-intro --></div></div>\n<!-- /wp:section -->',NULL,'http://baovietnhantho.awe7.com/storage/8e00381b-6e76-43ec-b081-79054043ad09/DVKH.jpg',0,NULL,'2019-06-04 08:36:47','2019-06-24 01:52:05'),(8,'pending','Hướng dẫn xem lại lịch sử đóng phí','huong-dan-xem-lai-lich-su-dong-phi','<!-- wp:section {\"className\":\"pt-0\"} -->\n<div class=\"wp-block-section md-section pt-0\"><div class=\"container\"><!-- wp:cgb/block-dichvu-intro {\"contentItems\":[{\"fileURL\":\"https://epos.baovietnhantho.com.vn/#/pages/open-help-center\",\"fileText\":\"Hướng dẫn My BVLife\"}],\"title\":\"Hướng dẫn xem lại lịch sử đóng phí\",\"text\":\"Đăng nhập My BVLife trên máy tính hoặc qua ứng dụng trên Apple Store trên iPhone hoặc CH Play trên máy điện thoại Android.\\u003cbr\\u003e- Chọn vào \\u003cstrong\\u003eTruy vấn Hợp đồng\\u003c/strong\\u003e/ tài khoản cá nhân \\u003cbr\\u003e- Màn hình hiển thị Danh sách các Hợp đồng bảo hiểm mà Khách hàng tham gia với Bảo Việt Nhân thọ \\u003cbr\\u003e- Chọn thông tin tra cứu tại Menu:\\u003cbr\\u003e [] Thông tin chung: Thông tin c